function initOrderPackage(){let orderpackageWarehouse=document.getElementById("package_order_form_warehouse");if(orderpackageWarehouse){orderpackageWarehouse.addEventListener("change",changeObjectPackageWarehouse,false)}}function changeObjectPackageWarehouse(){const requestModalName=new XMLHttpRequest;requestModalName.responseType="document";let PackageOrderForm=document.forms.package_order_form;let formData=new FormData;formData.append(this.getAttribute("name"),this.value);requestModalName.open(PackageOrderForm.getAttribute("method"),PackageOrderForm.getAttribute("action"),true);requestModalName.setRequestHeader("X-Requested-With","XMLHttpRequest");requestModalName.addEventListener("readystatechange",function(){if(requestModalName.readyState===4&&requestModalName.status===200){let result=requestModalName.response.getElementById("modal-body");document.getElementById("modal-body").replaceWith(result);initOrderPackage()}return false});requestModalName.send(formData)}